import React,{Component} from "react";

import axios from 'axios';
import { json } from "body-parser";
class Data extends Component {
  constructor(props) {
    super(props);
    this.state = { bugtitle: "", status: "",commment:"",priority:"", apiResponse:[] };
    this.buttonwasClicked = this.buttonwasClicked.bind(this);
  }

  componentDidMount(){

    fetch('http://localhost:4000/list').then(res=>res.json()).then(res=>this.setState({apiResponse:res}))
  }

  buttonwasClicked(){

    const book={bookId:1,bookTitle:"Book"};
      alert(this.refs.test.value);
      axios.post("http://localhost:4000/create", book)
        .then(() => console.log("Book Created"))
        .catch(err => {
          console.error(err);
        });
    }


  render() {
    const mystyle = {
      textAlign: "center",
      color: "white",
      backgroundColor: "DodgerBlue",
      padding: "10px",
      fontFamily: "Arial"
    };




    return (
      <div><table id="students">
        <tbody>
          <tr><td>Bug Title</td><td>Status</td> <td>Comment</td><td>Priority</td><td>Send</td></tr>
        {this.state.apiResponse.map( d =>{
          return  <tr>
               <td>{d.bugtitle}</td>
               <td>{d.status}</td>
               <td>{d.comment}</td>
               <td>{d.priority}</td>

               <input ref="test" type="text" name="email" placeholder="Email"></input>
            <button onClick={this.buttonwasClicked}>send</button></tr>
        })}</tbody></table>
        </div>
    );
  }
}

export default Data;
