import Home from "./Home";
import React, { Component } from "react";
import axios from "axios";
import { json } from "body-parser";

class AddBug extends Component {
  constructor(props) {
    super(props);
    this.state = { name: "", password: "", apiResponse: [] };
    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  componentDidMount() {
    fetch("http://localhost:4001/list")
      .then(res => res.json())
      .then(res => this.setState({ apiResponse: res }));
  }

  handleInputChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  handleSubmit = e => {
    e.preventDefault();

    const { name, password } = this.state;

    const info = {
      name,
      password
    };

    axios
      .post("http://localhost:4001/info", info)
      .then(() => console.log("Info Sent"))
      .catch(err => {
        console.error(err);
      });
  };

  render() {
    const mystyle = {
      textAlign: "center",
      color: "white",
      backgroundColor: "DodgerBlue",
      padding: "10px",
      fontFamily: "Arial"
    };

    return (
      <div>
          <Home></Home>
        <p>
          {this.state.apiResponse.map(d => {
            return (
              <h3>
                {d.name} and {d.password}
              </h3>
            );
          })}
        </p>
        <p style={mystyle}>Form</p>
        <form onSubmit={this.handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Name"
            onChange={this.handleInputChange}
          />
          <br />

          <input
            type="text"
            name="password"
            placeholder="Password"
            onChange={this.handleInputChange}
          />
          <br />
          <br />
          <input type="Submit"></input>
        </form>
      </div>
    );
  }
}
export default AddBug;
