import React from 'react';
import DataTable from './DataTable';
import Form from './Form';
import Message from './Message';
import API from './API';

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            datas: [],
            isEditForm: false,
            data: {
                bugtitle: "",
                status: "",
                comment: "",
                priority: ""
            },
            message: ""
        };

        this.deleteHandler = this.deleteHandler.bind(this);
        this.addHandler = this.addHandler.bind(this);
        this.updateHandler = this.updateHandler.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.showEditForm = this.showEditForm.bind(this);
    }

    componentDidMount() {
    API.getData().then(data => {
            console.log(data);
            this.setState({ datas: data.response })
        });
    }

    resetForm() {
        this.setState({
            data: {
                bugtitle: "",
                status: "",
                comment: "",
                priority: ""
            }
        });
    }

    handleChange(e) {
        this.setState({
            data: {
                ...this.state.data,
                [e.target.name]: e.target.value
            }
        });
    }

    showEditForm(data) {
        this.setState({ isEditForm: true, data: data });
    }

    async deleteHandler(id) {
        const deleteData = await API.deleteData(id);
        const message = deleteData.message;
        if (message.msgError) {
            this.setState({ message });
        }
        else {
            const data = await API.getData();
            this.setState({ message, datas: data.response })
        }
    }

    async updateHandler(e) {
        e.preventDefault();
        const updateData = await API.updateData(this.state.data);
        const message = updateData.message;
        if (message.msgError) {
            this.setState({ message });
        }
        else {
            const data = await API.getData();
            this.setState({ message, data: data.response })
        }
        this.setState({ isEditForm: false });
        this.resetForm();
    }

    async addHandler(e) {
        e.preventDefault();
        const postData = await API.createEmployee(this.state.data);
        const message = postData.message;
        if (message.msgError) {
            this.setState({ message });
        }
        else {
            const data = await API.getData();
            this.setState({ message, datas: data.response });
        }
        this.resetForm();
    }

    renderDataTable() {
        if (this.state.datas.length > 0) {
            return (
                <DataTable datas={this.state.datas}
                    deleteHandler={this.deleteHandler}
                    showEditForm={this.showEditForm} />
            );
        }
        return null;
    }

    renderForm() {
        return (
            <Form isEditForm={this.state.isEditForm}
                data={this.state.data}
                handleChange={this.handleChange}
                handler={!this.state.isEditForm ? this.addHandler : this.updateHandler} />
        );
    }

    renderMessage() {
        if (this.state.message === "")
            return null;
        return (
            <Message message={this.state.message} />
        );
    }

    render() {
        return (
            <div className="row">
                <div className="col"></div>
                <div className="col-10">
                    {this.renderDataTable()}
                    {this.renderForm()}
                    {this.renderMessage()}
                </div>
                <div className="col"></div>
            </div>
        )
    }
}

export default New;
