      .then(() => console.log("Info Sent"))
      .catch(err => {
        console.error(err);
      });
      return <Redirect to={{pathname:"/Dash"}}></Redirect>
  }
  return (
    <div style={mystyle}>

      <h2>Login with Google Account</h2>

      <GoogleLogin style={style}
        clientId="756401111838-vermrh4kd67ls2fnbqtaa7s377it5d99.apps.googleusercontent.com"
        buttonText="Login"
        onSuccess={responseGoogle}
        onFailure={responseGoogle}
        cookiePolicy={"single_host_origin"}
      />
    </div>
  );
}



export default Welcome;
